import React, { Component } from 'react';
import ProfileCard from "./profileCard";
import DrugCard from "./drugCard";
import NavBar from './navBar';
import user from './TempData/user';
import drugs from './TempData/drug';
import {NoverdoseRepo} from '../Api/NoverdoseRepo';

export default class MainPage extends React.Component {

    repo = new NoverdoseRepo();
    constructor(props) {
    super(props)

    this.state = {
        id: '',
        name: '',
        email: '',
        password: '',
        birthday: '',
        medications: '',
        profilePicUrl: '',
        user:''
    };
    }


    getUser(id) {
        console.log("GETTING USER")
        let t = this.repo.getUserById(id);
        console.log(t);
        console.log(JSON.stringify(t))
    }

    componentDidMount() {
        let id = +this.props.match.params.id;
        if (id) {
                this.repo.getUserById(id)
                    .then(curuser => {
                        this.setState(curuser)
                        console.log("GETTING USER")
                        console.log(JSON.stringify(curuser))
                        console.log(JSON.stringify(curuser.user))
                        console.log(curuser.user.name)
                        console.log(curuser.user.password)
                    }
                    );
        }
      }

    render() {
        var sampleUser = user;
        var allDrugs = drugs;
        return (
            <>
                <NavBar />
            <br></br>
                <div className="DrugCard" style={{
                    fontFamily: "sans-serif",
                    textAlign: "center",
                    background: "whitesmoke"
                }}>
                    <ProfileCard user={sampleUser}
                        cardColor={"#9EC0FE"} />
                <h1 style={{ textAlign: "center" }}>{sampleUser.name}'s Prescription</h1>
                    <div className="allCards"
                        style={{ "columns": "2" }}>
                    {allDrugs.map((x, y) => <DrugCard key={y}  {...x} />)}
                </div>
                </div>
                <h1>Hello</h1>
                <button className="btn btn-lg btn-primary" onClick={() => this.getUser(this.state.id)}>CLICK ME</button>
            </>
        );
    }
}
