import React, { useReducer } from "react";
import { Card, Button, Icon } from "semantic-ui-react";
// import "./DrugCard.css";
function ExtraContentAccordionClosed({ onClick }) {
  return (
    <Card.Content extra>
      <Button basic circular icon size="tiny" onClick={onClick}>
        <Icon name="plus circle" />
      </Button>
      Show More
    </Card.Content>
  );
}

function ExtraContentAccordionOpened({ content, onClick }) {
  return (
    <>
      <Card.Content extra>{content}</Card.Content>
      <Card.Content extra>
        <Button basic circular icon size="tiny" onClick={onClick}>
          <Icon name="minus circle" />
        </Button>
        Show Less
      </Card.Content>
    </>
  );
}

function ExtraContentAccordion({ open, content, onToggle }) {
  return open === true ? (
    <ExtraContentAccordionOpened content={content} onClick={onToggle} />
  ) : (
    <ExtraContentAccordionClosed onClick={onToggle} />
  );
}

function cardStateReducer(state, { type, payload }) {
  if (type === "TOGGLE") {
    var index = payload.card;
    var value = state[index];
    return [
      ...state.slice(0, index),
      !value,
      ...state.slice(index + 1, Infinity)
    ];
  }

  return state;
}

export default function DrugCard() {
  const [state, dispatch] = useReducer(this.cardStateReducer, [
    false,
    false,
    false,
    false,
    false,
    false
  ]);

  function toggleCard(card) {
    return function() {
      dispatch({ type: "TOGGLE", payload: { card } });
    };
  }

  return (
    <div className="App">
      <h1>Cute Pug's Prescription</h1>
      <Card.Group itemsPerRow={3}>
        <Card fluid color="blue" style={{ height: "100%" }}>
          <Card.Content header="Vitamin D" textAlign="center" />
          <Card.Content description="'Vitamin D helps your body absorb calcium. Calcium is one of the main building blocks of bone. A lack of vitamin D can lead to bone diseases such as osteoporosis or rickets. Vitamin D also has a role in your nerve, muscle, and immune systems. You can get vitamin D in three ways: through your skin (from sunlight), from your diet, and from supplements. Your body forms vitamin D naturally after exposure to sunlight. However, too much sun exposure can lead to skin aging and skin cancer.'" />
          <ExtraContentAccordion
            content="Extra content for card #0"
            onToggle={toggleCard(0)}
            open={state[0]}
          />
        </Card>
        <Card color="red" style={{ height: "100%" }}>
          <Card.Content header="Vitamin C" textAlign="center" />
          <Card.Content description="'Vitamin C is an antioxidant. It is important for your skin, bones, and connective tissue. It promotes healing and helps the body absorb iron. Vitamin C comes from fruits and vegetables. Good sources include citrus, red and green peppers, tomatoes, broccoli, and greens. Some juices and cereals have added vitamin C.'" />
          <ExtraContentAccordion
            content="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis ac commodo diam, et tincidunt massa. Sed aliquet tortor purus, in pulvinar enim mattis ac. Maecenas vestibulum cursus lorem, quis fermentum enim lacinia a. Ut nec feugiat nisl. Morbi finibus hendrerit diam, id iaculis nibh feugiat sed. Sed non justo turpis. Fusce neque quam, facilisis eu aliquam vitae, hendrerit nec nulla. Integer metus sapien, dictum eget viverra et, dictum in lectus. Integer vitae dolor ut libero dictum tristique eget non nunc. Suspendisse diam urna, pretium sed elementum sed, fermentum eu leo. Donec augue tortor, rhoncus id pulvinar ac, fringilla eu est. Duis et ante tristique dui molestie maximus at ut enim. Curabitur facilisis tempor lorem quis scelerisque. Maecenas enim leo, mollis at egestas in, vulputate eget risus."
            onToggle={toggleCard(1)}
            open={state[1]}
          />
        </Card>{" "}
        <Card color="blue" style={{ height: "100%" }}>
          <Card.Content header="Vitamin B" textAlign="center" />
          <Card.Content description="'Vitamin B6 is present in many foods and is added to other foods. The body needs vitamin B6 for many chemical reactions involved in metabolism. Vitamin B6 is involved in brain development during pregnancy and infancy. It also is involved in immune function.'" />
          <ExtraContentAccordion
            content="Extra content for card #2"
            onToggle={toggleCard(2)}
            open={state[2]}
          />
        </Card>{" "}
        <Card color="blue" style={{ height: "100%" }}>
          <Card.Content header="Vitamin A" textAlign="center" />
          <Card.Content description="'Vitamin A plays a role in your vision, bone growth, reproduction, cell functions, and immune system. Vitamin A is an antioxidant. It can come from plant or animal sources. Plant sources include colorful fruits and vegetables. Animal sources include liver and whole milk. Vitamin A is also added to foods like cereals." />
          <ExtraContentAccordion
            content="Extra content for card #3"
            onToggle={toggleCard(3)}
            open={state[3]}
          />
        </Card>{" "}
        <Card color="blue" style={{ height: "100%" }}>
          <Card.Content header="Vitamin E" textAlign="center" />
          <Card.Content description="'Vitamin E is an antioxidant. It plays a role in your immune system and metabolic processes. Most people get enough vitamin E from the foods they eat. Good sources of vitamin E include vegetable oils, margarine, nuts and seeds, and leafy greens. Vitamin E is added to foods like cereals. It is also available as a supplement.'" />
          <ExtraContentAccordion
            content="Extra content for card #4"
            onToggle={toggleCard(4)}
            open={state[4]}
          />
        </Card>{" "}
        <Card color="blue" style={{ height: "100%" }}>
          <Card.Content header="Vitamin K" textAlign="center" />
          <Card.Content description="'Vitamin K helps your body by making proteins for healthy bones and tissues. '" />
          <ExtraContentAccordion
            content="Extra content for card #5"
            onToggle={toggleCard(5)}
            open={state[5]}
          />
        </Card>{" "}
      </Card.Group>
    </div>
  );
}
