import React, { useReducer } from "react";
import { Card, Button, Icon } from "semantic-ui-react";
import './semantic-ui-css/semantic.min.css';
import "./DrugCard.css";
function ExtraContentAccordionClosed({ onClick }) {
  return (
    <Card.Content extra>
      <Button basic circular icon size="tiny" onClick={onClick}>
        <Icon name="plus circle" />
      </Button>
      Show More
    </Card.Content>
  );
}

function ExtraContentAccordionOpened({ content, onClick }) {
  return (
    <>
      <Card.Content extra>{content}</Card.Content>
      <Card.Content extra>
        <Button basic circular icon size="tiny" onClick={onClick}>
          <Icon name="minus circle" />
        </Button>
        Show Less
      </Card.Content>
    </>
  );
}

function ExtraContentAccordion({ open, content, onToggle }) {
  return open === true ? (
    <ExtraContentAccordionOpened content={content} onClick={onToggle} />
  ) : (
    <ExtraContentAccordionClosed onClick={onToggle} />
  );
}

function cardStateReducer(state, { type, payload }) {
  if (type === "TOGGLE") {
    var index = payload.card;
    var value = state[index];
    return [
      ...state.slice(0, index),
      !value,
      ...state.slice(index + 1, Infinity)
    ];
  }

  return state;
}

export default function DrugCard(allDrugs) {

    var drug = allDrugs.drugs;
  const [state, dispatch] = useReducer(cardStateReducer, [
    false,
    // false,
    // false,
    // false,
    // false,
    // false
  ]);

  function toggleCard(card) {
    return function() {
      dispatch({ type: "TOGGLE", payload: { card } });
    };
  }

    return (
    <div className="DrugCard">
      <h1>Cute Pug's Prescription</h1>
      <Card.Group itemsPerRow={3}>
        <Card fluid color="blue" style={{ height: "100%" }}>
                    <Card.Content header={allDrugs.name} textAlign="center" />
          <Card.Content description={allDrugs.description} />
          <ExtraContentAccordion
            content={allDrugs.sideEffect}
            onToggle={toggleCard(0)}
            open={state[0]}
          />
        </Card>
      </Card.Group>
            </div>
  );
}
