import React from 'react';
import { Row, Button, Form, Modal, Col } from 'react-bootstrap'
import './profilePage.css';

export default class ProfilePage extends React.Component {
	constructor(props) {
        super(props);
        this.state = {
			firstname: 'John',
			lastname: 'Smith',
			username: 'jsmith',
			hasEmptyField: false,
			edit: true
		}
    }


    render() {
            return (
            <>
            <form className="container">
                <h1>Account Editor</h1>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input type="text"
                        id="name"
                        name="name"
                        className="form-control"
                        value={this.state.name}
                        onChange={ e => this.setState({ name: e.target.value }) } />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input type="text"
                        id="email"
                        name="email"
                        className="form-control"
                        value={this.state.email}
                        onChange={ e => this.setState({ email: e.target.value }) } />
                </div>

                <div className="form-group">
                    <label htmlFor="isEmployee">
                        <input type="checkbox"
                            id="isEmployee"
                            name="isEmployee"
                            checked={this.state.isEmployee}
                            onChange={ e => this.setState({ isEmployee: e.target.checked }) } />
		                Is Employee
                    </label>
                </div>

                <div className="form-group">
                    <label htmlFor="departentId">Department</label>
                    <select id="departentId"
                        name="departentId"
                        className="form-control"
                        value={this.state.departmentId}
                        onChange={ e => this.setState({ departmentId: e.target.value }) }>
                        <option></option>
                        {
                            this.departments.map((d, i) => <option key={ i } value={ d.id }>{ d.name }</option>)
                        }
                    </select>
                </div>

            </form>
        </>
     );
    }
}
